import React, { useState } from "react";
import axios from "axios";

const EditedProduct = ({ product, onProductUpdated }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [productName, setProductName] = useState(product?.productname || "");
  const [description, setDescription] = useState(product?.description || "");
  const [price, setPrice] = useState(product?.price?.toString() || ""); // ใช้ string สำหรับราคา

  const handleOpenModal = () => {
    setIsModalOpen(true); // เปิด modal
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // ปิด modal
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const numericPrice = parseFloat(price);
    if (isNaN(numericPrice)) {
      console.error("Price must be a valid number");
      return;
    }

    try {
      // ส่งข้อมูลสินค้าไปยัง backend
      const response = await axios.put(`http://localhost:5050/products/${product.productid}`, {
        productname: productName,
        description: description,
        price: numericPrice,
      });

      if (response.status === 200) {
        // หากการแก้ไขสำเร็จ เรียก onProductUpdated เพื่อรีเฟรชข้อมูล
        onProductUpdated();
      }

      handleCloseModal(); // ปิด modal หลังจากบันทึกข้อมูล
    } catch (err) {
      console.error("Error updating product:", err);
    }
  };

  return (
    <div>
      {/* ปุ่ม Edit */}
      <button
        onClick={handleOpenModal}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Edit
      </button>

      {/* Modal */}
      {isModalOpen && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg w-96 relative z-60">
            <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">
              Edit Product
            </h2>
            <form onSubmit={handleSubmit}>
              {/* Product Name Input */}
              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  Product Name
                </label>
                <input
                  type="text"
                  className="w-full p-3 border border-gray-300 rounded bg-white focus:outline-none focus:ring focus:ring-blue-200"
                  value={productName}
                  onChange={(e) => setProductName(e.target.value)}
                />
              </div>

              {/* Description Input */}
              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  Description
                </label>
                <input
                  type="text"
                  className="w-full p-3 border border-gray-300 rounded bg-white focus:outline-none focus:ring focus:ring-blue-200"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                />
              </div>

              {/* Price Input */}
              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  Price
                </label>
                <input
                  type="text"
                  className="w-full p-3 border border-gray-300 rounded bg-white focus:outline-none focus:ring focus:ring-blue-200"
                  value={price}
                  onChange={(e) => setPrice(e.target.value)}
                />
              </div>

              {/* Save Button */}
              <button
                type="submit"
                className="w-full bg-blue-500 text-white font-medium py-3 rounded hover:bg-blue-700 transition duration-300"
              >
                Save Changes
              </button>
            </form>
            {/* Close Button */}
            <button
              onClick={handleCloseModal}
              className="absolute top-2 right-2 text-gray-500 text-2xl"
            >
              &times;
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default EditedProduct;
