import React, { useState } from "react";
import { jsPDF } from "jspdf";

const ReportsPage = () => {
  const [reportData, setReportData] = useState([
    { metric: "Stock", values: Array(12).fill(10) },
    { metric: "Stock Cost", values: Array(12).fill("$1000") },
    { metric: "Stock Value", values: Array(12).fill("$1200") },
    { metric: "Online Storefront", values: Array(12).fill("Active") },
    { metric: "Today's Sales", values: Array(12).fill("$500") },
    { metric: "This Month's Sales", values: Array(12).fill("$15000") },
  ]);

  const [newData, setNewData] = useState({ metric: "", values: Array(12).fill("") });

  const handleAddRow = () => {
    if (!newData.metric || newData.values.some((value) => value === "")) {
      alert("Please fill in all fields.");
      return;
    }

    setReportData((prevData) => [...prevData, newData]);
    setNewData({ metric: "", values: Array(12).fill("") });
  };

  const handleInputChange = (monthIndex, value) => {
    const updatedValues = [...newData.values];
    updatedValues[monthIndex] = value;
    setNewData({ ...newData, values: updatedValues });
  };

  const generatePDF = () => {
    const doc = new jsPDF();

    doc.setFontSize(16);
    doc.text("Monthly Report", 10, 10);
    doc.text("Year: 2024", 10, 20);

    let y = 30;
    reportData.forEach((row) => {
      doc.setFontSize(12);
      doc.text(`${row.metric}:`, 10, y);
      row.values.forEach((value, index) => {
        doc.text(`${index + 1}: ${value}`, 50 + index * 20, y);
      });
      y += 10;
    });

    doc.save("report.pdf");
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold text-gray-800 mb-6">Reports</h1>

      {/* Form to Add Data */}
      <div className="bg-white p-6 rounded-lg shadow-lg mb-6">
        <h2 className="text-2xl font-semibold text-gray-700 mb-4">Add New Data</h2>
        <div className="grid grid-cols-2 gap-4 mb-4">
          <input
            type="text"
            placeholder="Metric"
            value={newData.metric}
            onChange={(e) => setNewData({ ...newData, metric: e.target.value })}
            className="p-2 border border-gray-300 rounded"
          />
          {["JAN", "FEB", "MAR", "APR", "MAY", "JUN", "JUL", "AUG", "SEP", "OCT", "NOV", "DEC"].map((month, index) => (
            <input
              key={month}
              type="text"
              placeholder={month}
              value={newData.values[index]}
              onChange={(e) => handleInputChange(index, e.target.value)}
              className="p-2 border border-gray-300 rounded"
            />
          ))}
        </div>
        <button
          onClick={handleAddRow}
          className="bg-teal-500 text-white px-4 py-2 rounded hover:bg-teal-600"
        >
          Add Row
        </button>
      </div>

      {/* Report Table */}
      <div className="overflow-x-auto bg-white p-6 rounded-lg shadow-lg">
        <table className="table-auto w-full border-collapse border border-gray-200 text-left">
          <thead>
            <tr className="bg-gray-100">
              <th className="border border-gray-300 px-4 py-2">Reports</th>
              {["JAN", "FEB", "MAR", "APR", "MAY", "JUN", "JUL", "AUG", "SEP", "OCT", "NOV", "DEC"].map((month) => (
                <th key={month} className="border border-gray-300 px-4 py-2">
                  {month}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {reportData.map((row, rowIndex) => (
              <tr
                key={row.metric}
                className={rowIndex % 2 === 0 ? "bg-gray-50" : "bg-white"}
              >
                <td className="border border-gray-300 px-4 py-2 font-semibold">
                  {row.metric}
                </td>
                {row.values.map((value, index) => (
                  <td
                    key={index}
                    className="border border-gray-300 px-4 py-2 text-center"
                  >
                    {value}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <button
        onClick={generatePDF}
        className="mt-4 bg-teal-500 text-white px-4 py-2 rounded hover:bg-teal-600"
      >
        Export to PDF
      </button>
    </div>
  );
};

export default ReportsPage;
