import React, { useState } from "react";

const MainLayout = ({ children }) => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);

  return (
    <div className="min-h-screen flex">
      {/* Sidebar */}
      <div
        className={`${
          isSidebarOpen ? "block" : "hidden"
        } md:block w-64 bg-gray-800 text-white fixed md:relative`}
      >
        <div className="p-4 font-bold text-lg">POS System</div>
        <ul>
          <li className="p-4 hover:bg-gray-700">
            <a href="/">Dashboard</a>
          </li>
          <li className="p-4 hover:bg-gray-700">
            <a href="/inventory">Inventory</a>
          </li>
          <li className="p-4 hover:bg-gray-700">
            <a href="/sales">Sales</a>
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 bg-gray-100 p-6 ml-0 md:ml-64">
        <button
          className="block md:hidden p-4 bg-blue-500 text-white mb-4"
          onClick={() => setSidebarOpen(!isSidebarOpen)}
        >
          {isSidebarOpen ? "Close Menu" : "Open Menu"}
        </button>
        {children}
      </div>
    </div>
  );
};

export default MainLayout;
