import React, { useState } from "react";

const ProductForm = () => {
  const [isModalOpen, setIsModalOpen] = useState(false); // State สำหรับการเปิด/ปิด modal
  const [productName, setProductName] = useState("");
  const [quantity, setQuantity] = useState("");
  const [products, setProducts] = useState([]); // เก็บรายการสินค้าที่เพิ่มเข้ามา

  const handleAddProduct = () => {
    setIsModalOpen(true); // เปิด modal เมื่อกด "Add Product"
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // ปิด modal
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // เพิ่มสินค้าที่กรอกลงในรายการ
    setProducts([...products, { name: productName, quantity: quantity }]);
    setProductName(""); // ล้างฟอร์มหลังจากเพิ่ม
    setQuantity("");
    handleCloseModal(); // ปิด modal หลังจากบันทึกข้อมูล
  };

  return (
    <div>
      {/* ปุ่ม Add Product */}
      <button
        onClick={handleAddProduct}
        className="btn btn-primary mt-4"
      >
        Add Product
      </button>

      {/* Modal สำหรับเพิ่มสินค้า */}
      {isModalOpen && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50">
          <div className="bg-white p-8 rounded-lg shadow-lg w-96 relative">
            <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">
              Add Product
            </h2>
            <form onSubmit={handleSubmit}>
              {/* Product Name Input */}
              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  Product Name
                </label>
                <input
                  type="text"
                  className="input input-bordered w-full"
                  placeholder="Enter product name"
                  value={productName}
                  onChange={(e) => setProductName(e.target.value)}
                />
              </div>

              {/* Quantity Input */}
              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  Quantity
                </label>
                <input
                  type="number"
                  className="input input-bordered w-full"
                  placeholder="Enter quantity"
                  value={quantity}
                  onChange={(e) => setQuantity(e.target.value)}
                />
              </div>

              {/* ปุ่ม Save */}
              <button
                type="submit"
                className="btn btn-success w-full"
              >
                Add Product
              </button>
            </form>
            {/* ปุ่ม Close (กากบาท) */}
            <button
              onClick={handleCloseModal}
              className="absolute top-2 right-2 text-gray-500 text-2xl"
            >
              &times; {/* เครื่องหมาย X */}
            </button>
          </div>
        </div>
      )}

      {/* แสดงข้อมูลในตาราง */}
      <div className="mt-6">
        <table className="table w-full">
          <thead>
            <tr>
              <th>Product Name</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.quantity}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ProductForm;
