import React, { useState } from "react";
import ProductForm from "../components/layout/ui/ProductForm";
import { CSVLink } from "react-csv";
import { jsPDF } from "jspdf";

const ProductPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortType, setSortType] = useState("name");
  const [products, setProducts] = useState([
    { id: 1, name: "Product 1", category: "Category 1" },
    { id: 2, name: "Product 2", category: "Category 2" },
    { id: 3, name: "Product 3", category: "Category 1" },
    // เพิ่มข้อมูลสินค้าอื่น ๆ ตามต้องการ
  ]);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSort = (event) => {
    setSortType(event.target.value);
  };

  const filteredProducts = products
    .filter((product) =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (sortType === "name") {
        return a.name.localeCompare(b.name);
      } else {
        return a.category.localeCompare(b.category);
      }
    });

  const handleExportToPDF = () => {
    const doc = new jsPDF();
    doc.text("Product List", 10, 10);
    filteredProducts.forEach((product, index) => {
      doc.text(`${index + 1}. ${product.name} - ${product.category}`, 10, 20 + index * 10);
    });
    doc.save("products.pdf");
  };

  return (
    <div>
      <h1 className="text-3xl font-bold mb-4">Product</h1>
      <p>Manage your Product here.</p>

      <div className="mb-4">
        <label htmlFor="search" className="mr-2">Search: </label>
        <input
          id="search"
          type="text"
          value={searchTerm}
          onChange={handleSearch}
          className="border p-2"
          placeholder="Search by product name"
        />
      </div>

      <div className="mb-4">
        <label htmlFor="sort" className="mr-2">Sort by: </label>
        <select
          id="sort"
          value={sortType}
          onChange={handleSort}
          className="border p-2"
        >
          <option value="name">Name</option>
          <option value="category">Category</option>
        </select>
      </div>

      <div className="mb-4">
        <button
          onClick={handleExportToPDF}
          className="bg-blue-500 text-white p-2 mr-2"
        >
          Export to PDF
        </button>
        <CSVLink
          data={filteredProducts}
          filename="products.csv"
          className="bg-green-500 text-white p-2"
        >
          Export to CSV
        </CSVLink>
      </div>

      <div>
        <ul>
          {filteredProducts.map((product) => (
            <li key={product.id} className="mb-2">
              {product.name} - {product.category}
            </li>
          ))}
        </ul>
      </div>

      <ProductForm />
    </div>
  );
};

export default ProductPage;
