import React, { useState } from "react";
import { Link } from "react-router-dom"; // ใช้ Link สำหรับการนำทาง
import Header from "./ui/Header";

const MainLayout = ({ children }) => {
  const [isSalesOpen, setIsSalesOpen] = useState(false); // ใช้ state สำหรับเปิด/ปิด dropdown

  const user = {
    name: "John Doe",
    role: "administrator",
    profilePicture: "https://static.vecteezy.com/system/resources/thumbnails/005/544/718/small_2x/profile-icon-design-free-vector.jpg",
  };

  // ฟังก์ชันในการ toggle เมนู Sales Management
  const toggleSalesMenu = () => {
    setIsSalesOpen(!isSalesOpen);
  };

  return (
    <div className="min-h-screen flex flex-col">
      {/* Header */}
      <Header user={user} />

      {/* Sidebar and Main Content */}
      <div className="flex flex-1">
        {/* Sidebar */}
        <ul className="menu bg-base-200 w-56">
          <li className="menu-item m-1">
            <Link to="/">Dashboard</Link>
          </li>

          {/* Sales Dropdown */}
          <div className="dropdown dropdown-right">
            <div
              tabIndex={0}
              role="button"
              className="menu-item bg-base-200 rounded-box m-5"
              onClick={toggleSalesMenu} // เมื่อคลิกให้ toggle การเปิด/ปิด
            >
              Sales Management
            </div>
            {/* เมนูย่อยที่เปิด/ปิดตามสถานะ */}
            {isSalesOpen && (
              <ul
                tabIndex={0}
                className="dropdown-content menu bg-base-200 z-[1] w-54 p-2 shadow mt-4"
              >
                <li>
                  <Link to="/sales">Sales Product</Link>
                </li>
                <li>
                  <Link to="/salesHistory">Sales History</Link>
                </li>
                <li>
                  <Link to="/#">3</Link>
                </li>
              </ul>
            )}
          </div>

          <li className="menu-item m-1">
            <Link to="/product">Product management</Link>
          </li>
          <li className="menu-item mt-4 m-1">
            <Link to="/reports">Reports</Link>
          </li>
          <li className="menu-item mt-4 m-1">
            <Link to="/userManagement">User management</Link>
          </li>
        </ul>

        {/* Main Content */}
        <div className="flex-1 bg-gray-100 p-6">{children}</div>
      </div>
    </div>
  );
};

export default MainLayout;
