import React, { useState, useEffect } from "react";
import axios from "axios";
import { format } from "date-fns";

// ฟังก์ชันสำหรับแปลงวันที่ให้เป็นรูปแบบที่อ่านง่าย
const formatDate = (dateString) => {
  const date = new Date(dateString);
  return format(date, "d/MM/yyyy, HH:mm");
};

const RequestInventory = () => {
  const [requests, setRequests] = useState([]);
  const [fromBranch, setFromBranch] = useState("");
  const [toBranch, setToBranch] = useState("");
  const [product, setProduct] = useState("");
  const [quantity, setQuantity] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false); // State สำหรับเปิด/ปิด modal

  // ฟังก์ชันดึงข้อมูลคำขอทั้งหมด
  const fetchRequests = async () => {
    try {
      const response = await axios.get("http://localhost:5050/requests");
      setRequests(response.data.Data);
      setLoading(false);
    } catch (err) {
      setError("Failed to load requests");
      setLoading(false);
    }
  };

  // ฟังก์ชันสร้างคำขอใหม่
  const handleCreateRequest = async () => {
    const newRequest = {
      frombranchid: fromBranch,
      tobranchid: toBranch,
      productid: product,
      quantity,
      status: "pending",
    };

    try {
      const response = await axios.post("http://localhost:5050/requests", newRequest);
      if (response.status === 201) {
        fetchRequests(); // รีเฟรชคำขอหลังจากเพิ่มเสร็จ
        setFromBranch("");
        setToBranch("");
        setProduct("");
        setQuantity(0);
        setIsModalOpen(false); // ปิด modal เมื่อสร้างคำขอเสร็จ
      }
    } catch (err) {
      console.error("Error creating request:", err);
    }
  };

  // ฟังก์ชันอัปเดตสถานะคำขอจาก pending เป็น complete
  const handleUpdateStatus = async (requestId) => {
    try {
      const response = await axios.patch(`http://localhost:5050/requests/${requestId}`, { status: "complete" });
      if (response.status === 200) {
        fetchRequests(); // รีเฟรชข้อมูลคำขอหลังจากอัปเดตสถานะ
      }
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="p-4 bg-white">
      <h1 className="text-3xl font-bold text-black mb-4">Request Inventory</h1>

      <button
        onClick={() => setIsModalOpen(true)} // เปิด modal เมื่อคลิก
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Create New Request
      </button>

      {/* Modal สำหรับสร้างคำขอใหม่ */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-500 bg-opacity-75">
          <div className="bg-white p-6 rounded shadow-lg w-96">
            <h2 className="text-xl font-semibold text-black mb-4">Create New Request</h2>
            <div className="mb-4">
              <input
                type="text"
                value={fromBranch}
                onChange={(e) => setFromBranch(e.target.value)}
                placeholder="From Branch ID"
                className="input input-bordered w-full mb-2"
              />
              <input
                type="text"
                value={toBranch}
                onChange={(e) => setToBranch(e.target.value)}
                placeholder="To Branch ID"
                className="input input-bordered w-full mb-2"
              />
              <input
                type="text"
                value={product}
                onChange={(e) => setProduct(e.target.value)}
                placeholder="Product ID"
                className="input input-bordered w-full mb-2"
              />
              <input
                type="number"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
                placeholder="Quantity"
                className="input input-bordered w-full mb-2"
              />
            </div>
            <div className="flex justify-between">
              <button
                onClick={handleCreateRequest}
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Create Request
              </button>
              <button
                onClick={() => setIsModalOpen(false)} // ปิด modal เมื่อคลิก
                className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      <h2 className="text-xl font-semibold text-black mb-4">Request List</h2>
      <div className="overflow-x-auto">
        <table className="table w-full table-striped">
          <thead>
            <tr>
              <th className="text-black">Request ID</th>
              <th className="text-black">From Branch</th>
              <th className="text-black">To Branch</th>
              <th className="text-black">Product</th>
              <th className="text-black">Quantity</th>
              <th className="text-black">Status</th>
              <th className="text-black">Created At</th>
              <th className="text-black">Actions</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((request) => (
              <tr key={request.requestid}>
                <td className="text-black">{request.requestid}</td>
                <td className="text-black">{request.frombranchid}</td>
                <td className="text-black">{request.tobranchid}</td>
                <td className="text-black">{request.productid}</td>
                <td className="text-black">{request.quantity}</td>
                <td className="text-black">{request.status}</td>
                <td className="text-black">{formatDate(request.createdat)}</td>
                <td className="text-black">
                  {request.status === "pending" && (
                    <button
                      onClick={() => handleUpdateStatus(request.requestid)}
                      className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-700"
                    >
                      Mark as Complete
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default RequestInventory;
