import React, { useState, useEffect } from "react";
import axios from "axios";
import ExportButtons from "../components/layout/ui/ExportButtons";
import { format } from "date-fns";
import { FiArrowUp, FiArrowDown } from "react-icons/fi"; // ไอคอนสำหรับการเรียงลำดับ

// ฟังก์ชันสำหรับแปลงวันที่ให้เป็นรูปแบบที่อ่านง่าย
const formatDate = (dateString) => {
  const date = new Date(dateString);
  return format(date, "d/MM/yyyy, HH:mm");
};

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [products, setProducts] = useState({});
  const [branches, setBranches] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedInventory, setSelectedInventory] = useState(null);

  // สำหรับการค้นหา
  const [searchTerm, setSearchTerm] = useState("");

  // สำหรับการเรียงลำดับ
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "asc" });

  // ฟังก์ชันดึงข้อมูล
  const fetchInventory = async () => {
    try {
      const [inventoryResponse, productResponse, branchResponse] = await Promise.all([
        axios.get("http://localhost:5050/inventory"),
        axios.get("http://localhost:5050/products"),
        axios.get("http://localhost:5050/branches"),
      ]);

      const productMap = {};
      productResponse.data.Data.forEach((product) => {
        productMap[product.productid] = product.productname;
      });

      const branchMap = {};
      branchResponse.data.Data.forEach((branch) => {
        branchMap[branch.branchid] = {
          bname: branch.bname,
          location: branch.location,
        };
      });

      setInventory(inventoryResponse.data.Data);
      setProducts(productMap);
      setBranches(branchMap);
      setLoading(false);
    } catch (err) {
      setError("Failed to load inventory data");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInventory();
  }, []);

  const handleViewDetails = (inventory) => {
    setSelectedInventory(inventory);
  };

  const handleCloseModal = () => {
    setSelectedInventory(null);
  };

  // ฟังก์ชันสำหรับการเรียงลำดับ
  const handleSort = (key) => {
    let direction = "asc";
    if (sortConfig.key === key && sortConfig.direction === "asc") {
      direction = "desc";
    }
    setSortConfig({ key, direction });

    const sortedData = [...inventory].sort((a, b) => {
      const aValue = key === "productid" ? products[a[key]] : key === "branchid" ? branches[a[key]]?.bname : a[key];
      const bValue = key === "productid" ? products[b[key]] : key === "branchid" ? branches[b[key]]?.bname : b[key];

      if (aValue < bValue) return direction === "asc" ? -1 : 1;
      if (aValue > bValue) return direction === "asc" ? 1 : -1;
      return 0;
    });

    setInventory(sortedData);
  };

  // กรองข้อมูลสำหรับการค้นหา
  const filteredInventory = inventory.filter((item) => {
    const productName = products[item.productid]?.toLowerCase() || "";
    const branchName = branches[item.branchid]?.bname.toLowerCase() || "";
    return (
      productName.includes(searchTerm.toLowerCase()) ||
      branchName.includes(searchTerm.toLowerCase())
    );
  });

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="p-4 bg-white">
      <h1 className="text-3xl font-bold text-black mb-4">Inventory</h1>
      <p className="text-black mb-4">Manage your Inventory here.</p>

      <div className="flex justify-between items-center mb-4">
        <ExportButtons filteredProducts={inventory} />
        <input
          type="text"
          placeholder="Search..."
          className="border border-gray-300 rounded px-4 py-2"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="overflow-x-auto">
        <table className="table w-full table-striped">
          <thead>
            <tr>
              <th
                className="text-black cursor-pointer flex items-center"
                onClick={() => handleSort("productid")}
              >
                Product Name
                {sortConfig.key === "productid" && sortConfig.direction === "asc" ? (
                  <FiArrowUp className="ml-2" />
                ) : sortConfig.key === "productid" && sortConfig.direction === "desc" ? (
                  <FiArrowDown className="ml-2" />
                ) : null}
              </th>
              <th
                className="text-black cursor-pointer flex items-center"
                onClick={() => handleSort("branchid")}
              >
                Branch Name
                {sortConfig.key === "branchid" && sortConfig.direction === "asc" ? (
                  <FiArrowUp className="ml-2" />
                ) : sortConfig.key === "branchid" && sortConfig.direction === "desc" ? (
                  <FiArrowDown className="ml-2" />
                ) : null}
              </th>
              <th
                className="text-black cursor-pointer flex items-center"
                onClick={() => handleSort("quantity")}
              >
                Quantity
                {sortConfig.key === "quantity" && sortConfig.direction === "asc" ? (
                  <FiArrowUp className="ml-2" />
                ) : sortConfig.key === "quantity" && sortConfig.direction === "desc" ? (
                  <FiArrowDown className="ml-2" />
                ) : null}
              </th>
              <th
                className="text-black cursor-pointer flex items-center"
                onClick={() => handleSort("updatedat")}
              >
                Updated At
                {sortConfig.key === "updatedat" && sortConfig.direction === "asc" ? (
                  <FiArrowUp className="ml-2" />
                ) : sortConfig.key === "updatedat" && sortConfig.direction === "desc" ? (
                  <FiArrowDown className="ml-2" />
                ) : null}
              </th>
              <th className="text-black">Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredInventory.map((item) => (
              <tr key={item.inventoryid}>
                <td className="text-black">{products[item.productid]}</td>
                <td className="text-black">{branches[item.branchid]?.bname}</td>
                <td className="text-black">{item.quantity}</td>
                <td className="text-black">{formatDate(item.updatedat)}</td>
                <td className="text-black">
                  <button
                    onClick={() => handleViewDetails(item)}
                    className="bg-blue-500 text-white font-medium px-4 py-3 rounded hover:bg-blue-600 transition duration-300"
                  >
                    View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {selectedInventory && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md relative">
            <h2 className="text-3xl font-bold mb-6 text-gray-800 text-center">
              Inventory Details
            </h2>
            <div className="space-y-4">
              <p className="text-gray-700">
                <span className="font-semibold">Product Name:</span> {products[selectedInventory.productid]}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">Branch Name:</span> {branches[selectedInventory.branchid]?.bname}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">Location:</span> {branches[selectedInventory.branchid]?.location}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">Quantity:</span> {selectedInventory.quantity}
              </p>
              <p className="text-gray-700">
                <span className="font-semibold">Updated At:</span> {formatDate(selectedInventory.updatedat)}
              </p>
            </div>
            <button
              onClick={handleCloseModal}
              className="w-full bg-blue-500 text-white font-medium px-6 py-3 mt-6 rounded-md hover:bg-blue-600 transition duration-300"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default InventoryPage;
