import React, { useState, useEffect } from "react";
import axios from "axios";
import ExportButtons from "../components/layout/ui/ExportButtons";
import SortByDropdown from "../components/layout/ui/SortByDropdown";
import { format } from "date-fns";

const formatDate = (dateString) => {
  const date = new Date(dateString);
  return format(date, "d/MM/yyyy, HH:mm");
};

const InventoryPage = () => {
  const [inventory, setInventory] = useState([]);
  const [products, setProducts] = useState({});
  const [branches, setBranches] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedInventory, setSelectedInventory] = useState(null);
  const [sortKey, setSortKey] = useState("productid");
  const [sortDirection, setSortDirection] = useState("asc");
  const [newRequest, setNewRequest] = useState({
    frombranchid: "",
    tobranchid: "",
    productid: "",
    quantity: 0,
    status: "pending",
  });
  const [requests, setRequests] = useState([]);
  
  const fetchInventory = async () => {
    try {
      const [inventoryResponse, productResponse, branchResponse] = await Promise.all([
        axios.get("http://localhost:5050/inventory"),
        axios.get("http://localhost:5050/products"),
        axios.get("http://localhost:5050/branches"),
      ]);

      const productMap = {};
      productResponse.data.Data.forEach((product) => {
        productMap[product.productid] = product.productname;
      });

      const branchMap = {};
      branchResponse.data.Data.forEach((branch) => {
        branchMap[branch.branchid] = {
          bname: branch.bname,
          location: branch.location,
        };
      });

      setInventory(inventoryResponse.data.Data);
      setProducts(productMap);
      setBranches(branchMap);
      setLoading(false);
    } catch (err) {
      setError("Failed to load inventory data");
      setLoading(false);
    }
  };

  const fetchRequests = async () => {
    try {
      const response = await axios.get("http://localhost:5050/requests");
      setRequests(response.data.Data);
    } catch (err) {
      console.error("Error fetching requests:", err);
    }
  };

  useEffect(() => {
    fetchInventory();
    fetchRequests();
  }, []);

  const handleSortChange = (key, direction) => {
    setSortKey(key);
    setSortDirection(direction);

    const sortedData = [...inventory].sort((a, b) => {
      const aValue =
        key === "productid" ? products[a[key]] : key === "branchid" ? branches[a[key]]?.bname : a[key];
      const bValue =
        key === "productid" ? products[b[key]] : key === "branchid" ? branches[b[key]]?.bname : b[key];

      if (aValue < bValue) return direction === "asc" ? -1 : 1;
      if (aValue > bValue) return direction === "asc" ? 1 : -1;
      return 0;
    });

    setInventory(sortedData);
  };

  const handleRequestChange = (e) => {
    setNewRequest({
      ...newRequest,
      [e.target.name]: e.target.value,
    });
  };

  const handleCreateRequest = async () => {
    try {
      const response = await axios.post("http://localhost:5050/requests", newRequest);
      if (response.status === 201) {
        fetchRequests();
        setNewRequest({
          frombranchid: "",
          tobranchid: "",
          productid: "",
          quantity: 0,
          status: "pending",
        });
      }
    } catch (err) {
      console.error("Error creating request:", err);
    }
  };

  const handleUpdateStatus = async (requestId) => {
    try {
      const response = await axios.patch(`http://localhost:5050/requests/${requestId}`, { status: "complete" });
      if (response.status === 200) {
        fetchRequests();
      }
    } catch (err) {
      console.error("Error updating request status:", err);
    }
  };

  const sortOptions = [
    { key: "productid", label: "Product Name" },
    { key: "branchid", label: "Branch Name" },
    { key: "quantity", label: "Quantity" },
    { key: "updatedat", label: "Updated At" },
  ];

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="p-4 bg-white">
      <h1 className="text-3xl font-bold text-black mb-4">Inventory</h1>
      <p className="text-black mb-4">Manage your Inventory here.</p>

      <div className="flex space-x-4 mb-4">
        <ExportButtons filteredProducts={inventory} />
      </div>

      {/* Button to open "Request Inventory" modal */}
      <div className="mb-4">
        <button
          onClick={() => setNewRequest({ ...newRequest, frombranchid: "", tobranchid: "", productid: "", quantity: 0 })}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Request Inventory
        </button>
      </div>

      {/* Modal for Creating Inventory Request */}
      {newRequest && (
        <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md relative">
          <h2 className="text-3xl font-bold mb-6 text-gray-800 text-center">Create Inventory Request</h2>
          <div className="space-y-4">
            <input
              type="text"
              name="frombranchid"
              value={newRequest.frombranchid}
              onChange={handleRequestChange}
              placeholder="From Branch ID"
              className="input input-bordered w-full max-w-xs"
            />
            <input
              type="text"
              name="tobranchid"
              value={newRequest.tobranchid}
              onChange={handleRequestChange}
              placeholder="To Branch ID"
              className="input input-bordered w-full max-w-xs"
            />
            <input
              type="text"
              name="productid"
              value={newRequest.productid}
              onChange={handleRequestChange}
              placeholder="Product ID"
              className="input input-bordered w-full max-w-xs"
            />
            <input
              type="number"
              name="quantity"
              value={newRequest.quantity}
              onChange={handleRequestChange}
              placeholder="Quantity"
              className="input input-bordered w-full max-w-xs"
            />
            <button
              onClick={handleCreateRequest}
              className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Create Request
            </button>
          </div>
        </div>
      )}

      {/* ใช้ SortByDropdown และส่งค่า sortOptions */}
      <SortByDropdown
        onSortChange={handleSortChange}
        currentSortKey={sortKey}
        currentSortDirection={sortDirection}
        sortOptions={sortOptions} // ส่งตัวเลือกที่สามารถเลือกได้
      />

      <div className="overflow-x-auto">
        <table className="table w-full table-striped">
          <thead>
            <tr>
              <th className="text-black">Product Name</th>
              <th className="text-black">Branch Name</th>
              <th className="text-black">Quantity</th>
              <th className="text-black">Updated At</th>
              <th className="text-black">Actions</th>
            </tr>
          </thead>
          <tbody>
            {inventory.map((item) => (
              <tr key={item.inventoryid}>
                <td className="text-black">{products[item.productid]}</td>
                <td className="text-black">{branches[item.branchid]?.bname}</td>
                <td className="text-black">{item.quantity}</td>
                <td className="text-black">{formatDate(item.updatedat)}</td>
                <td className="text-black">
                  <button
                    onClick={() => handleViewDetails(item)}
                    className="bg-base-200 text-white font-medium px-4 py-3 rounded hover:bg-gray-700 transition duration-300"
                  >
                    View
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Request List */}
      <h2 className="text-xl font-semibold mb-4">Inventory Requests</h2>
      <div className="overflow-x-auto">
        <table className="table w-full table-striped">
          <thead>
            <tr>
              <th className="text-black">Request ID</th>
              <th className="text-black">From Branch</th>
              <th className="text-black">To Branch</th>
              <th className="text-black">Product ID</th>
              <th className="text-black">Quantity</th>
              <th className="text-black">Status</th>
              <th className="text-black">Actions</th>
            </tr>
          </thead>
          <tbody>
            {requests.map((request) => (
              <tr key={request.requestid}>
                <td className="text-black">{request.requestid}</td>
                <td className="text-black">{branches[request.frombranchid]?.bname}</td>
                <td className="text-black">{branches[request.tobranchid]?.bname}</td>
                <td className="text-black">{request.productid}</td>
                <td className="text-black">{request.quantity}</td>
                <td className="text-black">{request.status}</td>
                <td className="text-black">
                  {request.status === "pending" && (
                    <button
                      onClick={() => handleUpdateStatus(request.requestid)}
                      className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-700"
                    >
                      Mark as Complete
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default InventoryPage;
