import React, { useState, useEffect } from "react";
import axios from "axios";
import { HiTrash } from "react-icons/hi";

const SalesPage = () => {
  const [products, setProducts] = useState([]);
  const [branches, setBranches] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [cart, setCart] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);

  const fetchData = async () => {
    try {
      const [productRes, branchRes, inventoryRes] = await Promise.all([
        axios.get("http://localhost:5050/products"),
        axios.get("http://localhost:5050/branches"),
        axios.get("http://localhost:5050/inventory"),
      ]);
      setProducts(productRes.data.Data);
      setBranches(branchRes.data.Data);
      setInventory(inventoryRes.data.Data);
    } catch (err) {
      console.error("Error fetching data", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleBranchChange = (event) => {
    setSelectedBranch(event.target.value);
    setCart([]); // Clear the cart when branch is changed
    setTotalAmount(0); // Reset the total amount
  };

  const handleAddToCart = (product, quantity) => {
    if (!selectedBranch || selectedBranch === "all") {
      alert("โปรดเลือกสาขาก่อน");
      return;
    }

    const inventoryItem = inventory.find(
      (item) => item.productid === product.productid && item.branchid === selectedBranch
    );
    if (!inventoryItem) {
      alert("Product not available in the selected branch");
      return;
    }

    if (inventoryItem.quantity === 0) {
      alert("Out of stock");
      return;
    }

    // Add or update product in the cart
    setCart((prevCart) => {
      const updatedCart = [...prevCart];
      const existingProduct = updatedCart.find((item) => item.productid === product.productid);
      if (existingProduct) {
        existingProduct.quantity += quantity;
      } else {
        updatedCart.push({ ...product, quantity, branchid: selectedBranch });
      }
      return updatedCart;
    });

    updateTotalAmount();
  };

  const handleRemoveFromCart = (productid) => {
    setCart((prevCart) => {
      const newCart = prevCart.filter((item) => item.productid !== productid);
      return newCart;
    });
    updateTotalAmount();
  };

  const handleQuantityChange = (productid, newQuantity) => {
    setCart((prevCart) => {
      const updatedCart = prevCart.map((item) =>
        item.productid === productid ? { ...item, quantity: newQuantity } : item
      );
      return updatedCart;
    });
    updateTotalAmount();
  };

  const updateTotalAmount = () => {
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotalAmount(total);
  };

  const handleCheckout = async () => {
    if (!selectedBranch) {
      alert("Select branch before checkout");
      return;
    }
  
    if (cart.length === 0) {
      alert("Your cart is empty select some products to checkout");
      return;
    }
  
    const saleItems = cart.map((item) => ({
      productid: item.productid,
      quantity: item.quantity,
      price: item.price,
      totalprice: item.price * item.quantity,
    }));
  
    const saleData = {
      employeeid: "8a714024-471a-420f-8abb-46509d0cd74e",
      branchid: selectedBranch,
      saleitems: saleItems,
    };
  
    try {
      await axios.post("http://localhost:5050/sales", saleData);
      alert("Sale completed successfully!");
      setCart([]);
      setTotalAmount(0);
    } catch (err) {
      console.error("Error during sale", err);
      alert("Failed to complete the sale.");
    }
  };

  const getInventoryByBranch = (branchId) => {
    if (branchId === "all") {
      return inventory;
    } else {
      return inventory.filter((item) => item.branchid === branchId);
    }
  };

  const filterInventoryByProduct = () => {
    return getInventoryByBranch(selectedBranch).reduce((acc, item) => {
      const product = products.find((p) => p.productid === item.productid);
      if (product) {
        const existingProduct = acc.find((p) => p.productid === item.productid);
        if (existingProduct) {
          existingProduct.totalQuantity += item.quantity;
        } else {
          acc.push({ ...product, totalQuantity: item.quantity });
        }
      }
      return acc;
    }, []);
  };

  return (
    <div className="p-4 bg-white">
      <h1 className="text-3xl font-bold text-black mb-4">Sales Product</h1>

      <div className="mb-6 flex items-center justify-between">
        <select
          id="branch-select"
          value={selectedBranch}
          onChange={handleBranchChange}
          className="ml-4 w-1/3 bg-white border border-gray-300 text-black font-semibold p-3 rounded-lg shadow-md focus:outline-none focus:ring-2 focus:ring-teal-500 transition duration-200 ease-in-out"
        >
          <option value="all" className="text-gray-500">Select Branch</option>
          {branches.map((branch) => (
            <option key={branch.branchid} value={branch.branchid} className="text-black">
              {branch.bname}
            </option>
          ))}
        </select>
      </div>



      <div className="flex">
        <div className="w-4/5 mr-4">
          <h2 className="text-xl text-black font-bold mb-4">Product Lists</h2>
          <div className="grid grid-cols-4 gap-4">
            {selectedBranch && selectedBranch !== "all" ? (
              filterInventoryByProduct().map((product) => {
                const stock =
                  getInventoryByBranch(selectedBranch).find(
                    (item) => item.productid === product.productid
                  )?.quantity || 0;
                return (
                  <button
                    key={product.productid}
                    onClick={() => handleAddToCart(product, 1)}
                    className={`card border border-slate-300 shadow-xl p-4 flex flex-col justify-between items-center transition-transform transform hover:border-teal-700 scale-105 ${stock === 0 ? "opacity-50" : ""}`}
                  >
                    <figure className="flex justify-center items-center h-2/3 w-full">
                      <img
                        src={product.imageurl}
                        alt={product.productname}
                        className="max-h-full max-w-full"
                      />
                    </figure>
                    <div className="text-center my-2">
                      <h2 className="text-black font-semibold text-sm">{product.productname}</h2>
                      {/* <p className="text-xs text-black">Qty: {stock}</p> */}
                      <p className="text-xs font-semibold text-black">Price: ${product.price}</p>
                    </div>
                  </button>
                );
              })
            ) : (
              <p className="text-center col-span-4">Please select a branch to view products.</p>
            )}
          </div>
        </div>

        <div className="w-2/5">
          <h2 className="text-xl text-black font-bold mb-4">Your Cart</h2>
          <div className="border p-6 rounded h-96 overflow-y-auto">
            {cart.map((item) => (
              <div key={item.productid} className="text-black justify-between mb-2">
                <div>{item.productname}</div>
                <div className="flex items-center">
                  <input
                    type="number"
                    value={item.quantity}
                    onChange={(e) =>
                      handleQuantityChange(item.productid, parseInt(e.target.value))
                    }
                    className="text-black bg-white w-16 border border-2 border-teal-600 p-1 rounded"
                    min="1"
                  />
                  <span className="text-black ml-2">
                    {item.quantity} x ${item.price} = ${item.price * item.quantity}
                  </span>
                  <button
                    onClick={() => handleRemoveFromCart(item.productid)}
                    className="ml-2 text-red-600"
                  >
                    <HiTrash size={20} />
                  </button>
                </div>
              </div>
            ))}
            <div className="mt-4 text-black font-bold">Total: ${totalAmount}</div>
            <button
              onClick={handleCheckout}
              className="btn border-none mt-4 w-full bg-teal-500 text-white p-2 rounded hover:bg-teal-600 transition duration-300 mt-4"
            >
              Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SalesPage;
