import React from "react";
import { CSVLink } from "react-csv";
import { jsPDF } from "jspdf";

// ฟังก์ชันสำหรับการ Export ไปยัง PDF
const handleExportToPDF = (data, columns, filename = "export.pdf") => {
  const doc = new jsPDF();
  doc.text("Data Export", 10, 10);

  // ตั้งค่าตำแหน่งสำหรับเริ่มต้นพิมพ์ตาราง
  let y = 20;

  // พิมพ์หัวตาราง
  columns.forEach((column, index) => {
    doc.text(column, 10 + index * 40, y);
  });

  // พิมพ์ข้อมูล
  data.forEach((item, index) => {
    y += 10;
    columns.forEach((column, colIndex) => {
      doc.text(item[column], 10 + colIndex * 40, y);
    });
  });

  // บันทึก PDF
  doc.save(filename);
};

const ExportButtons = ({ filteredTables, columns }) => {
  return (
    <div className="flex items-center space-x-4 mb-4">
      <button
        onClick={() => handleExportToPDF(filteredTables, columns, "products.pdf")}
        className="bg-base-200 text-white px-6 py-3 rounded hover:bg-gray-700 transition duration-300 mt-4"
      >
        Export to PDF
      </button>
      <CSVLink
        data={filteredTables}
        filename="products.csv"
        className="bg-base-200 text-white px-6 py-3 rounded hover:bg-gray-700 transition duration-300 mt-4"
      >
        Export to CSV
      </CSVLink>
    </div>
  );
};

export default ExportButtons;
