import React, { useState, useEffect } from "react";
import axios from "axios";

const SalesPage = () => {
  const [products, setProducts] = useState([]);
  const [inventory, setInventory] = useState({});
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const [branches, setBranches] = useState([]);
  const [selectedBranch, setSelectedBranch] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [productsResponse, inventoryResponse, branchesResponse] = await Promise.all([
          axios.get("http://localhost:5050/products"),
          axios.get("http://localhost:5050/inventory"),
          axios.get("http://localhost:5050/branches")
        ]);

        const inventoryMap = {};
        inventoryResponse.data.Data.forEach((item) => {
          inventoryMap[item.productid] = item;
        });

        setProducts(productsResponse.data.Data);
        setInventory(inventoryMap);
        setBranches(branchesResponse.data.Data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching data", err);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleAddToCart = (product) => {
    setCart([...cart, product]);
    setTotal(total + product.price);
  };

  const handleRemoveFromCart = (productId) => {
    const newCart = cart.filter(item => item.productid !== productId);
    const productToRemove = cart.find(item => item.productid === productId);
    setCart(newCart);
    setTotal(total - productToRemove.price);
  };

  const handleBranchChange = (e) => {
    setSelectedBranch(e.target.value);
  };

  const filteredProducts = selectedBranch
    ? products.filter((product) => inventory[product.id]?.branchid === selectedBranch)
    : products;

  if (loading) return <div>Loading...</div>;

  return (
    <div className="p-4 bg-white">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-3xl text-black font-bold">Sales Product</h1>
      </div>

      {/* Search Bar */}
      <div className="flex items-center mb-4">
        <input
          type="text"
          placeholder="Search for products"
          className="border bg-white border-gray-300 p-2 rounded w-full mr-2"
        />
        <button className="btn border-none text-white bg-teal-500 px-4 py-2 rounded hover:bg-teal-600">
            Search
        </button>
      </div>

      {/* Branch Dropdown */}
      <div className="mb-4">
        <select
          value={selectedBranch}
          onChange={handleBranchChange}
          className="border p-2 rounded"
        >
          <option value="">Select Branch</option>
          {branches.map((branch) => (
            <option key={branch.id} value={branch.id}>
              {branch.name}
            </option>
          ))}
        </select>
      </div>

      {/* Product List */}
      <div className="grid grid-cols-5 gap-4">
        {filteredProducts.map((product) => (
          <div
            key={product.id}
            className="border border-gray-300 p-4 rounded flex flex-col items-center"
          >
            <div className="w-24 h-24 bg-gray-200 mb-2 rounded">
              <img
                src={product.imageUrl || "https://via.placeholder.com/150"}
                alt={product.productname}
                className="w-full h-full object-cover rounded"
              />
            </div>
            <div className="text-black text-lg font-bold">{product.productcode}</div>
            <div className="text-black text-sm mb-2">{product.productname}</div>
            <div className="text-black text-sm">Price: {product.price}</div>
            <div className="text-black text-sm">Stock: {inventory[product.id]?.quantity}</div>
            <button
              onClick={() => handleAddToCart(product)}
              className="mt-2 btn bg-teal-500 text-white px-4 py-2 rounded"
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      {/* Cart Section */}
      <div
        className="fixed top-0 right-0 mt-6 mr-6 bg-white shadow-lg rounded-lg p-4 w-1/3"
        style={{ zIndex: 100 }}
      >
        <h2 className="text-2xl font-bold">Your Cart</h2>
        {cart.length > 0 ? (
          <div>
            <ul>
              {cart.map((item, index) => (
                <li key={index} className="flex justify-between items-center">
                  <span>{item.productname} - ${item.price}</span>
                  <button
                    onClick={() => handleRemoveFromCart(item.productid)}
                    className="text-red-500 hover:text-red-700"
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>
            <div className="mt-4 text-xl font-bold">Total: ${total}</div>
            <button className="mt-4 bg-teal-500 text-white px-4 py-2 rounded hover:bg-teal-600">
              Place Order
            </button>
          </div>
        ) : (
          <div>Your cart is empty.</div>
        )}
      </div>
    </div>
  );
};

export default SalesPage;
