import React, { useState, useEffect } from "react";
import axios from "axios";
import InventoryForm from "../components/layout/ui/InventoryForm";
import ExportButtons from "../components/layout/ui/ExportButtons";

const InventoryPage = () => {
  const [inventories, setInventories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ฟังก์ชันดึงข้อมูลสินค้า
  const fetchInventories = async () => {
    try {
      const response = await axios.get("http://localhost:5050/inventories");
      setInventories(response.data.Data); // ตั้งค่าข้อมูลที่ได้จาก API
      setLoading(false);
    } catch (err) {
      setError("Failed to load inventories");
      setLoading(false);
    }
  };

  // ฟังก์ชันลบสินค้า
  const handleDeleteInventory = async (inventoryId) => {
    try {
      const response = await axios.delete(
        `http://localhost:5050/inventories/${inventoryId}`
      );
      if (response.status === 200) {
        // หากการลบสำเร็จ รีเฟรชข้อมูลสินค้า
        fetchInventories();
      }
    } catch (err) {
      console.error("Error deleting inventory:", err);
    }
  };

  // รีเฟรชข้อมูลสินค้าเมื่อมีการเพิ่มสินค้าใหม่
  const handleInventoryAdded = () => {
    fetchInventories();
  };

  useEffect(() => {
    fetchInventories();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="p-4 bg-white">
      <h1 className="text-3xl font-bold text-black mb-4">Inventory</h1>
      <p className="text-black mb-4">Manage your Inventory here.</p>

      <div className="flex space-x-4 mb-4">
        <InventoryForm onInventoryAdded={handleInventoryAdded} />
        <ExportButtons filteredInventories={inventories} />
      </div>

      <div className="overflow-x-auto">
        <table className="table w-full table-striped">
          <thead>
            <tr>
              <th className="text-black">Name</th>
              <th className="text-black">Description</th>
              <th className="text-black">Price</th>
              <th className="text-black">Actions</th> {/* เพิ่มคอลัมน์ Actions สำหรับปุ่มลบ */}
            </tr>
          </thead>
          <tbody>
            {inventories.map((inventory) => (
              <tr key={inventory.inventoryid}>
                <td className="text-black">{inventory.inventoryname}</td>
                <td className="text-black">{inventory.description}</td>
                <td className="text-black">{inventory.price}</td>
                <td className="text-black">
                  {/* ปุ่มลบ */}
                  <button
                    onClick={() => handleDeleteInventory(inventory.inventoryid)}
                    className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-700"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default InventoryPage;
