import React from "react";

// ฟังก์ชันสำหรับการเปลี่ยนแปลงการเรียงลำดับ
const SortByButtons = ({ onSortChange, currentSortKey, currentSortDirection, sortOptions }) => {
  const handleSortChange = (key) => {
    let direction = "asc";
    if (currentSortKey === key && currentSortDirection === "asc") {
      direction = "desc";
    }
    onSortChange(key, direction); // ส่งค่าไปยังฟังก์ชันใน parent component
  };

  return (
    <div className="flex space-x-4">
      {sortOptions.map((option) => (
        <button
          key={option.key}
          onClick={() => handleSortChange(option.key)}
          className={`btn ${currentSortKey === option.key ? " bg-base-200 text-white font-medium py-3 rounded hover:bg-gray-700 transition duration-300 mb-2" : ""}`}
        >
          {option.label}
        </button>
      ))}
    </div>
  );
};

export default SortByButtons;
