import React, { useState, useEffect } from "react";

const ProductsList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ดึงข้อมูลสินค้าจาก API เมื่อเพจโหลด
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:3000/products");
        if (!response.ok) {
          throw new Error("Failed to fetch products");
        }
        const data = await response.json();
        setProducts(data.Data); // กำหนดข้อมูลสินค้า
        setLoading(false); // เปลี่ยนสถานะให้แสดงผลลัพธ์
      } catch (err) {
        setError("Failed to load products");
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="p-4">
      <h1 className="text-3xl font-bold text-black mb-4">Product List</h1>
      <table className="table w-full">
        <thead>
          <tr>
            <th className="text-black">Product ID</th>
            <th className="text-black">Product Name</th>
            <th className="text-black">Description</th>
            <th className="text-black">Price</th>
            <th className="text-black">Units Per Box</th>
            <th className="text-black">Created At</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.productid}>
              <td>{product.productid}</td>
              <td>{product.productname}</td>
              <td>{product.description}</td>
              <td>{product.price}</td>
              <td>{product.unitsperbox}</td>
              <td>{new Date(product.createdat).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductsList;
