import React, { useState, useEffect } from "react";
import axios from "axios";

const RequestInventory = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [requests, setRequests] = useState([]); // รักษาข้อมูลคำขอสินค้าทั้งหมด
  const [branches, setBranches] = useState([]); // เก็บข้อมูลสาขา
  const [products, setProducts] = useState([]); // เก็บข้อมูลสินค้า
  const [newRequest, setNewRequest] = useState({
    frombranchid: "",
    tobranchid: "",
    productid: "",
    quantity: 0,
    status: "pending",
  });

  // ดึงข้อมูลคำขอ
  const fetchRequests = async () => {
    try {
      const response = await axios.get("http://localhost:5050/Requests");
      if (Array.isArray(response.data.Data)) {
        setRequests(response.data.Data);
      } else {
        console.error("Expected an array of requests, but got:", response.data);
      }
    } catch (err) {
      console.error("Error fetching requests:", err);
    }
  };

  // ดึงข้อมูล branch
  const fetchBranches = async () => {
    try {
      const response = await axios.get("http://localhost:5050/Branches");
      setBranches(response.data.Data || []);
    } catch (err) {
      console.error("Error fetching branches:", err);
    }
  };

  // ดึงข้อมูล product
  const fetchProducts = async () => {
    try {
      const response = await axios.get("http://localhost:5050/Products");
      setProducts(response.data.Data || []);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  useEffect(() => {
    if (isModalOpen) {
      fetchRequests();
      fetchBranches();
      fetchProducts();
    }
  }, [isModalOpen]);

  const handleAddRequest = async () => {
    try {
      await axios.post("http://localhost:5050/Requests", newRequest);
      fetchRequests(); // รีเฟรชรายการคำขอหลังจากเพิ่มคำขอ
      setNewRequest({
        frombranchid: "",
        tobranchid: "",
        productid: "",
        quantity: 0,
        status: "pending",
      });
    } catch (err) {
      console.error("Error adding request:", err);
    }
  };

  const handleUpdateStatus = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5050/Requests/${requestId}`, {
        status: "complete",
      });
      fetchRequests();
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  const getBranchName = (branchId) => {
    const branch = branches.find((b) => b.branchid === branchId);
    return branch ? branch.branchname : "Unknown Branch";
  };

  const getProductName = (productId) => {
    const product = products.find((p) => p.productid === productId);
    return product ? product.productname : "Unknown Product";
  };

  return (
    <div>
      <button
        onClick={() => setIsModalOpen(true)}
        className="bg-blue-500 text-white px-6 py-3 rounded hover:bg-blue-600 transition duration-300 mt-4"
      >
        Request Inventory
      </button>

      {isModalOpen && (
        <div
          className="fixed inset-0 items-center bg-black bg-opacity-50 z-50 overflow-y-auto"
          onClick={(e) => e.target === e.currentTarget && setIsModalOpen(false)}
        >
          <div className="bg-white p-8 rounded-lg shadow-lg max-w-7xl mx-auto m-10 relative z-60 flex">
            <div className="w-1/2 pr-4">
              <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">
                Request Inventory
              </h2>
              <form>
                {/* ฟอร์มสำหรับกรอกข้อมูลคำขอ */}
                {/* โค้ดส่วนฟอร์มเหมือนเดิม */}
              </form>
            </div>

            <div className="w-1/2 pl-4 overflow-x-auto">
              <h3 className="text-xl font-semibold text-gray-700 mb-4">Requests</h3>
              <table className="table w-full">
                <thead>
                  <tr>
                    <th>Request ID</th>
                    <th>From Branch</th>
                    <th>To Branch</th>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {requests.map((request) => (
                    <tr key={request.requestid}>
                      <td>{request.requestid}</td>
                      <td>{getBranchName(request.frombranchid)}</td>
                      <td>{getBranchName(request.tobranchid)}</td>
                      <td>{getProductName(request.productid)}</td>
                      <td>{request.quantity}</td>
                      <td>{request.status}</td>
                      <td>
                        {request.status === "pending" && (
                          <button
                            onClick={() => handleUpdateStatus(request.requestid)}
                            className="bg-yellow-500 text-white font-medium px-4 py-2 rounded hover:bg-yellow-600"
                          >
                            Mark as Complete
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <button
              onClick={() => setIsModalOpen(false)}
              className="absolute top-2 right-2 text-gray-500 text-2xl"
            >
              &times;
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RequestInventory;
