import React, { useState, useEffect } from "react";
import axios from "axios";

const RequestInventory = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [requests, setRequests] = useState([]);
  const [branches, setBranches] = useState([]);
  const [products, setProducts] = useState([]);
  const [newRequest, setNewRequest] = useState({
    frombranchid: "",
    tobranchid: "",
    productid: "",
    quantity: 0,
    status: "pending",
  });

  // Fetch data from API
  const fetchRequests = async () => {
    try {
      const response = await axios.get("http://localhost:5050/Requests");
      setRequests(response.data.Data || []);
    } catch (err) {
      console.error("Error fetching requests:", err);
    }
  };

  const fetchBranches = async () => {
    try {
      const response = await axios.get("http://localhost:5050/branches");
      setBranches(response.data.Data || []);
    } catch (err) {
      console.error("Error fetching branches:", err);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await axios.get("http://localhost:5050/products");
      setProducts(response.data.Data || []);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  useEffect(() => {
    fetchBranches();
    fetchProducts();
    fetchRequests();
  }, []);

  // Add new request
  const handleAddRequest = async () => {
    try {
      await axios.post("http://localhost:5050/Requests", newRequest);
      fetchRequests();
      setNewRequest({
        frombranchid: "",
        tobranchid: "",
        productid: "",
        quantity: 0,
        status: "pending",
      });
    } catch (err) {
      console.error("Error adding request:", err);
    }
  };

  // Update request status
  const handleUpdateStatus = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5050/Requests/${requestId}`, {
        status: "complete",
      });
      fetchRequests();
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  return (
    <div>
      <button
        onClick={() => setIsModalOpen(true)}
        className="bg-blue-500 text-white px-6 py-3 rounded hover:bg-blue-600 transition duration-300 mt-4"
      >
        Request Inventory
      </button>

      {/* Add Request Modal */}
      {isModalOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-50 flex justify-center items-center"
          onClick={(e) => e.target === e.currentTarget && setIsModalOpen(false)}
        >
          <div className="bg-white p-8 rounded-lg shadow-lg max-w-7xl w-full relative z-60 overflow-y-auto max-h-screen">
            <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">
              Request Inventory Management
            </h2>

            {/* Add Request Form */}
            <div className="mb-8">
              <h3 className="text-xl font-semibold text-black mb-4">
                Add New Request
              </h3>
              <form>
                <div className="grid grid-cols-2 gap-4 mb-4">
                  <div>
                    <label className="block text-black font-medium mb-2">
                      From Branch
                    </label>
                    <select
                      className="w-full p-3 border border-gray-300 rounded bg-white"
                      value={newRequest.frombranchid}
                      onChange={(e) =>
                        setNewRequest({ ...newRequest, frombranchid: e.target.value })
                      }
                    >
                      <option value="">Select Branch</option>
                      {branches.map((branch) => (
                        <option key={branch.branchid} value={branch.branchid}>
                          {branch.bname}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="block text-black font-medium mb-2">
                      To Branch
                    </label>
                    <select
                      className="w-full p-3 border border-gray-300 rounded bg-white"
                      value={newRequest.tobranchid}
                      onChange={(e) =>
                        setNewRequest({ ...newRequest, tobranchid: e.target.value })
                      }
                    >
                      <option value="">Select Branch</option>
                      {branches.map((branch) => (
                        <option key={branch.branchid} value={branch.branchid}>
                          {branch.bname}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>
                <div className="grid grid-cols-2 gap-4 mb-4">
                  <div>
                    <label className="block text-black font-medium mb-2">
                      Product
                    </label>
                    <select
                      className="w-full p-3 border border-gray-300 rounded bg-white"
                      value={newRequest.productid}
                      onChange={(e) =>
                        setNewRequest({ ...newRequest, productid: e.target.value })
                      }
                    >
                      <option value="">Select Product</option>
                      {products.map((product) => (
                        <option key={product.productid} value={product.productid}>
                          {product.productname}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="block text-black font-medium mb-2">
                      Quantity
                    </label>
                    <input
                      type="number"
                      className="w-full p-3 border border-gray-300 rounded bg-white"
                      value={newRequest.quantity}
                      onChange={(e) =>
                        setNewRequest({
                          ...newRequest,
                          quantity: parseInt(e.target.value),
                        })
                      }
                    />
                  </div>
                </div>
                <button
                  type="button"
                  className="bg-green-500 text-white font-medium py-3 px-6 rounded hover:bg-green-600 transition duration-300"
                  onClick={handleAddRequest}
                >
                  Add Request
                </button>
              </form>
            </div>

            {/* Tables Section */}
            <div className="mb-6">
              <h3 className="text-xl font-semibold mb-4">Requests</h3>
              <table className="table-auto w-full border-collapse border border-gray-300 mb-4">
                <thead>
                  <tr>
                    <th className="border px-4 py-2">Request ID</th>
                    <th className="border px-4 py-2">Status</th>
                    <th className="border px-4 py-2">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {requests.map((request) => (
                    <tr key={request.requestid}>
                      <td className="border px-4 py-2">{request.requestid}</td>
                      <td className="border px-4 py-2">{request.status}</td>
                      <td className="border px-4 py-2">
                        {request.status === "pending" && (
                          <button
                            onClick={() => handleUpdateStatus(request.requestid)}
                            className="bg-yellow-500 text-white px-2 py-1 rounded hover:bg-yellow-600 "
                          >
                            Mark as Complete
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <div className="grid grid-cols-2 gap-8">
              {/* Branches Table */}
              <div>
                <h3 className="text-xl font-semibold mb-4">Branches</h3>
                <table className="table-auto w-full border-collapse border border-gray-300">
                  <thead>
                    <tr>
                      <th className="border px-4 py-2">Branch ID</th>
                      <th className="border px-4 py-2">Branch Name</th>
                    </tr>
                  </thead>
                  <tbody>
                    {branches.map((branch) => (
                      <tr key={branch.branchid}>
                        <td className="border px-4 py-2">{branch.branchid}</td>
                        <td className="border px-4 py-2">{branch.bname}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {/* Products Table */}
              <div>
                <h3 className="text-xl font-semibold mb-4">Products</h3>
                <table className="table-auto w-full border-collapse border border-gray-300">
                  <thead>
                    <tr>
                      <th className="border px-4 py-2">Product ID</th>
                      <th className="border px-4 py-2">Product Name</th>
                    </tr>
                  </thead>
                  <tbody>
                    {products.map((product) => (
                      <tr key={product.productid}>
                        <td className="border px-4 py-2">{product.productid}</td>
                        <td className="border px-4 py-2">{product.productname}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            <button
              onClick={() => setIsModalOpen(false)}
              className="absolute top-2 right-2 text-gray-500 text-1xl"
            >
              &times;
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RequestInventory;
