import React, { useState, useEffect } from "react";
import axios from "axios"; // หากคุณใช้ axios ในการดึงข้อมูล

const ProductPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ฟังก์ชันดึงข้อมูลสินค้าพร้อมสต็อก
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // ดึงข้อมูลจาก API ที่รวมข้อมูล Product และ Inventory
        const response = await axios.get("http://localhost:3000/products-with-inventory"); // เปลี่ยน URL ตามที่ใช้งาน
        setProducts(response.data.Data); // ตั้งค่าข้อมูลที่ได้จาก API
        setLoading(false);
      } catch (err) {
        setError("Failed to load products");
        setLoading(false);
      }
    };

    fetchProducts();
  }, []); // เรียกฟังก์ชันเมื่อ component โหลดครั้งแรก

  // แสดงข้อมูลในกรณีที่ยังโหลดข้อมูล
  if (loading) {
    return <div>Loading...</div>;
  }

  // แสดงข้อมูลในกรณีที่เกิดข้อผิดพลาด
  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="p-4 bg-white">
      <h1 className="text-3xl font-bold text-black mb-4">Product</h1>
      <p className="text-black mb-4">Manage your Product here.</p>

      {/* ปุ่ม Add Product และ Export PDF, Export CSV */}
      <div className="flex space-x-4 mb-4">
        <button className="bg-blue-500 text-white px-6 py-3 rounded hover:bg-blue-700">
          Add Product
        </button>
        <button className="bg-gray-500 text-white px-6 py-3 rounded hover:bg-gray-700">
          Export PDF
        </button>
        <button className="bg-green-500 text-white px-6 py-3 rounded hover:bg-green-700">
          Export CSV
        </button>
      </div>

      {/* แสดงตารางข้อมูลสินค้า */}
      <div className="overflow-x-auto">
        <table className="table w-full table-striped">
          <thead>
            <tr>
              <th className="text-black">Name</th>
              <th className="text-black">Description</th>
              <th className="text-black">Price</th>
              <th className="text-black">Units Per Box</th>
              <th className="text-black">Branches</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.productid}>
                <td className="text-black">{product.productname}</td>
                <td className="text-black">{product.description}</td>
                <td className="text-black">{product.price}</td>
                <td className="text-black">{product.unitsperbox}</td>
                <td className="text-black">
                  {/* แสดงข้อมูลสาขาที่มีสินค้า */}
                  {product.inventory.map((item) => (
                    <div key={item.inventoryid}>
                      Branch: {item.branchid}, Quantity: {item.quantity}
                    </div>
                  ))}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ProductPage;
