import React, { useState, useEffect } from "react";
import axios from "axios";
import { HiTrash } from "react-icons/hi";

const SalesPage = () => {
  const [products, setProducts] = useState([]);
  const [branches, setBranches] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [cart, setCart] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);

  const fetchData = async () => {
    try {
      const [productRes, branchRes, inventoryRes] = await Promise.all([
        axios.get("http://localhost:5050/products"),
        axios.get("http://localhost:5050/branches"),
        axios.get("http://localhost:5050/inventory"),
      ]);
      setProducts(productRes.data.Data);
      setBranches(branchRes.data.Data);
      setInventory(inventoryRes.data.Data);
    } catch (err) {
      console.error("Error fetching data", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleBranchChange = (event) => {
    setSelectedBranch(event.target.value);
    setCart([]); // Clear the cart when branch is changed
    setTotalAmount(0); // Reset the total amount
  };

  const handleAddToCart = (product, quantity) => {
    if (!selectedBranch || selectedBranch === "all") {
      alert("โปรดเลือกสาขาก่อน");
      return;
    }

    const inventoryItem = inventory.find(
      (item) => item.productid === product.productid && item.branchid === selectedBranch
    );
    if (!inventoryItem) {
      alert("Product not available in the selected branch");
      return;
    }

    if (inventoryItem.quantity === 0) {
      alert("Out of stock");
      return;
    }

    const productInCart = cart.find((item) => item.productid === product.productid);
    if (productInCart) {
      productInCart.quantity += quantity;
    } else {
      cart.push({ ...product, quantity, branchid: selectedBranch });
    }
    setCart([...cart]);
    updateTotalAmount();
  };

  const handleRemoveFromCart = (productid) => {
    const newCart = cart.filter((item) => item.productid !== productid);
    setCart(newCart);
    updateTotalAmount();
  };

  const handleQuantityChange = (productid, newQuantity) => {
    const updatedCart = cart.map((item) =>
      item.productid === productid ? { ...item, quantity: newQuantity } : item
    );
    setCart(updatedCart);
    updateTotalAmount();
  };

  const updateTotalAmount = () => {
    let total = 0;
    cart.forEach((item) => {
      total += item.price * item.quantity;
    });
    setTotalAmount(total);
  };

  const handleCheckout = async () => {
    if (!selectedBranch) {
      alert("โปรดเลือกสาขาก่อน");
      return;
    }
    const saleItems = cart.map((item) => ({
      productid: item.productid,
      quantity: item.quantity,
      price: item.price,
      totalprice: item.price * item.quantity,
    }));
    const saleData = {
      employeeid: "8a714024-471a-420f-8abb-46509d0cd74e",
      branchid: selectedBranch,
      saleitems: saleItems,
    };
    try {
      await axios.post("http://localhost:5050/sales", saleData);
      alert("Sale completed successfully!");
      setCart([]);
      setTotalAmount(0);
    } catch (err) {
      console.error("Error during sale", err);
      alert("Failed to complete the sale.");
    }
  };

  const filterInventoryByProduct = () => {
    return inventory
      .filter((item) => item.branchid === selectedBranch)
      .map((inv) => {
        const product = products.find((p) => p.productid === inv.productid);
        return { ...product, stock: inv.quantity };
      });
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold text-gray-800 mb-6">Sales Product</h1>

      <div className="mb-4">
        <select
          value={selectedBranch || ""}
          onChange={handleBranchChange}
          className="w-1/3 p-3 border rounded shadow-sm"
        >
          <option value="">Select Branch</option>
          {branches.map((branch) => (
            <option key={branch.branchid} value={branch.branchid}>
              {branch.bname}
            </option>
          ))}
        </select>
      </div>

      <div className="flex gap-8">
        {/* Product Section */}
        <div className="flex-1">
          <h2 className="text-xl font-semibold mb-4">Products</h2>
          <div className="grid grid-cols-4 gap-4">
            {selectedBranch &&
              filterInventoryByProduct().map((product) => (
                <div
                  key={product.productid}
                  className={`bg-white rounded-lg shadow-md p-4 flex flex-col justify-between ${
                    product.stock === 0 ? "opacity-50" : ""
                  }`}
                >
                  <div className="h-24 flex items-center justify-center mb-4">
                    <img
                      src="https://via.placeholder.com/100"
                      alt={product.productname}
                      className="max-h-full"
                    />
                  </div>
                  <h3 className="text-center font-semibold text-gray-800">
                    {product.productname}
                  </h3>
                  <p className="text-center text-sm text-gray-600">
                    Stock: {product.stock}
                  </p>
                  <p className="text-center text-sm text-gray-600">
                    Price: ${product.price}
                  </p>
                  <button
                    onClick={() => handleAddToCart(product, 1)}
                    className="mt-4 bg-teal-500 text-white rounded p-2 disabled:bg-gray-300"
                    disabled={product.stock === 0}
                  >
                    Add to Cart
                  </button>
                </div>
              ))}
          </div>
        </div>

        {/* Cart Section */}
        <div className="w-1/3">
          <h2 className="text-xl font-semibold mb-4">Cart</h2>
          <div className="bg-white rounded-lg shadow-md p-6">
            {cart.map((item) => (
              <div key={item.productid} className="flex justify-between items-center mb-4">
                <div>
                  <h4 className="font-semibold">{item.productname}</h4>
                  <p className="text-sm text-gray-500">
                    {item.quantity} x ${item.price}
                  </p>
                </div>
                <div className="flex items-center">
                  <button
                    onClick={() => handleRemoveFromCart(item.productid)}
                    className="text-red-600"
                  >
                    <HiTrash size={20} />
                  </button>
                </div>
              </div>
            ))}
            <div className="mt-4 font-semibold text-lg">
              Total: ${totalAmount}
            </div>
            <button
              onClick={handleCheckout}
              className="mt-4 w-full bg-teal-600 text-white rounded p-2"
            >
              Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SalesPage;
