import React from "react";
import { Pie, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  CategoryScale,
  LinearScale,
  BarElement,
} from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const DashboardPage = () => {
  const salesSummary = [
    { branch: "Branch A", sales: 5000 },
    { branch: "Branch B", sales: 3000 },
    { branch: "Branch C", sales: 4500 },
  ];

  const keyMetrics = [
    { label: "Total Sales", value: 12500 },
    { label: "Active Users", value: 45 },
    { label: "Low Stock Alerts", value: 10 },
  ];

  // Data for Pie Chart
  const pieData = {
    labels: salesSummary.map((data) => data.branch),
    datasets: [
      {
        data: salesSummary.map((data) => data.sales),
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
        hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
      },
    ],
  };

  // Data for Bar Chart
  const barData = {
    labels: keyMetrics.map((metric) => metric.label),
    datasets: [
      {
        label: "Metrics",
        data: keyMetrics.map((metric) => metric.value),
        backgroundColor: "#36A2EB",
      },
    ],
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen text-black">
      <h1 className="text-2xl font-bold mb-4">Dashboard</h1>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
        {keyMetrics.map((metric, index) => (
          <div
            key={index}
            className="bg-white shadow rounded-lg p-4 flex flex-col items-center justify-center"
          >
            <h2 className="text-lg font-semibold">{metric.label}</h2>
            <p className="text-xl font-bold text-blue-500">{metric.value}</p>
          </div>
        ))}
      </div>

      {/* Pie Chart */}
      <div className="bg-white shadow rounded-lg p-6 mb-6">
        <h2 className="text-lg font-semibold mb-4">Sales Summary (Pie Chart)</h2>
        <Pie data={pieData} />
      </div>

      {/* Bar Chart */}
      <div className="bg-white shadow rounded-lg p-6 mb-6">
        <h2 className="text-lg font-semibold mb-4">Key Metrics (Bar Chart)</h2>
        <Bar data={barData} options={{ responsive: true, maintainAspectRatio: false }} />
      </div>

      {/* Sales Summary Table */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-lg font-semibold mb-4">Sales Summary (Table)</h2>
        <table className="w-full border-collapse border border-gray-300">
          <thead>
            <tr className="bg-gray-200">
              <th className="border border-gray-300 p-2 text-left">Branch</th>
              <th className="border border-gray-300 p-2 text-left">Sales</th>
            </tr>
          </thead>
          <tbody>
            {salesSummary.map((data, index) => (
              <tr key={index}>
                <td className="border border-gray-300 p-2">{data.branch}</td>
                <td className="border border-gray-300 p-2">${data.sales.toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default DashboardPage;
