import React, { useState, useEffect } from "react";
import axios from "axios";

const RequestInventory = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [requests, setRequests] = useState([]);  // รักษาข้อมูลคำขอสินค้าทั้งหมด
  const [newRequest, setNewRequest] = useState({
    frombranchid: "",
    tobranchid: "",
    productid: "",
    quantity: 0,
    status: "pending",
  });

  const fetchRequests = async () => {
    try {
      const response = await axios.get("http://localhost:5050/Requests");
      if (Array.isArray(response.data.Data)) {
        setRequests(response.data.Data); // ใช้ response.data.Data แทน response.data
      } else {
        console.error("Expected an array of requests, but got:", response.data);
      }
    } catch (err) {
      console.error("Error fetching requests:", err);
    }
  };

  useEffect(() => {
    if (isModalOpen) {
      fetchRequests(); // ดึงข้อมูลคำขอสินค้าทุกครั้งเมื่อเปิด modal
    }
  }, [isModalOpen]);

  const handleAddRequest = async () => {
    try {
      await axios.post("http://localhost:5050/Requests", newRequest);
      fetchRequests(); // รีเฟรชรายการคำขอหลังจากเพิ่มคำขอ
      setNewRequest({
        frombranchid: "",
        tobranchid: "",
        productid: "",
        quantity: 0,
        status: "pending",
      });
    } catch (err) {
      console.error("Error adding request:", err);
    }
  };

  const handleUpdateStatus = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5050/Requests/${requestId}`, {
        status: "complete", // เปลี่ยนสถานะเป็น complete
      });
      fetchRequests(); // รีเฟรชรายการคำขอหลังจากอัปเดตสถานะ
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  return (
    <div className="flex justify-center items-start mt-8">
      <div className="flex w-full max-w-screen-xl space-x-8">
        {/* ตารางแสดงคำขอสินค้าทั้งหมด */}
        <div className="flex-1 p-6 bg-white rounded-lg shadow-lg overflow-x-auto">
          <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">Requests</h2>

          <div className="overflow-x-auto">
            <table className="table w-full">
              <thead>
                <tr>
                  <th>Request ID</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {/* ตรวจสอบให้แน่ใจว่า requests เป็นอาร์เรย์ก่อนที่จะใช้ map */}
                {Array.isArray(requests) &&
                  requests.map((request) => (
                    <tr key={request.requestid}>
                      <td>{request.requestid}</td>
                      <td>{request.status}</td>
                      <td>
                        {request.status === "pending" && (
                          <button
                            onClick={() => handleUpdateStatus(request.requestid)}
                            className="bg-yellow-500 text-white font-medium px-4 py-2 rounded hover:bg-yellow-600"
                          >
                            Mark as Complete
                          </button>
                        )}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* ฟอร์มสำหรับเพิ่มคำขอสินค้า */}
        <div className="w-1/3 bg-white p-6 rounded-lg shadow-lg">
          <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">
            Request Inventory
          </h2>
          <form>
            <div className="mb-4">
              <label className="block text-gray-600 font-medium mb-2">
                From Branch ID
              </label>
              <input
                type="text"
                className="w-full p-3 border border-gray-300 rounded bg-white"
                value={newRequest.frombranchid}
                onChange={(e) =>
                  setNewRequest({ ...newRequest, frombranchid: e.target.value })
                }
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-600 font-medium mb-2">
                To Branch ID
              </label>
              <input
                type="text"
                className="w-full p-3 border border-gray-300 rounded bg-white"
                value={newRequest.tobranchid}
                onChange={(e) =>
                  setNewRequest({ ...newRequest, tobranchid: e.target.value })
                }
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-600 font-medium mb-2">
                Product ID
              </label>
              <input
                type="text"
                className="w-full p-3 border border-gray-300 rounded bg-white"
                value={newRequest.productid}
                onChange={(e) =>
                  setNewRequest({ ...newRequest, productid: e.target.value })
                }
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-600 font-medium mb-2">
                Quantity
              </label>
              <input
                type="number"
                className="w-full p-3 border border-gray-300 rounded bg-white"
                value={newRequest.quantity}
                onChange={(e) =>
                  setNewRequest({ ...newRequest, quantity: parseInt(e.target.value) })
                }
              />
            </div>

            <button
              type="button"
              className="w-full bg-green-600 text-white font-medium py-3 rounded-lg hover:bg-green-700 transition duration-300"
              onClick={handleAddRequest}
            >
              Add Request
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default RequestInventory;
