import React, { useState, useEffect } from "react";
import axios from "axios";

const RequestInventory = ({ onProductAdded }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [requests, setRequests] = useState([]);  // Make sure this is initialized as an empty array
  const [productName, setProductName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(""); // Price input
  const [newRequest, setNewRequest] = useState({
    frombranchid: "",
    tobranchid: "",
    productid: "",
    quantity: 0,
    status: "pending",
  });

  const fetchRequests = async () => {
    try {
      const response = await axios.get("http://localhost:5050/Requests");

      // Check if the response data is an array before setting the state
      if (Array.isArray(response.data)) {
        setRequests(response.data);
      } else {
        console.error("Expected an array of requests, but got:", response.data);
      }
    } catch (err) {
      console.error("Error fetching requests:", err);
    }
  };

  useEffect(() => {
    if (isModalOpen) {
      fetchRequests();
    }
  }, [isModalOpen]);

  const handleAddProduct = () => {
    setIsModalOpen(true); // Open modal
  };

  const handleCloseModal = () => {
    setIsModalOpen(false); // Close modal
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const numericPrice = parseFloat(price);

    if (isNaN(numericPrice)) {
      console.error("Price must be a valid number");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5050/products", {
        productname: productName,
        description: description,
        price: numericPrice,
        unitsperbox: 30,
      });

      if (response.data) {
        onProductAdded();
      }
      handleCloseModal();
    } catch (err) {
      console.error("Error adding product:", err);
    }
  };

  const handleRequestSubmit = async () => {
    try {
      await axios.post("http://localhost:5050/Requests", newRequest);
      fetchRequests(); // Refresh request list
      setNewRequest({
        frombranchid: "",
        tobranchid: "",
        productid: "",
        quantity: 0,
        status: "pending",
      });
    } catch (err) {
      console.error("Error adding request:", err);
    }
  };

  const handleUpdateStatus = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5050/Requests/${requestId}`, {
        status: "complete",
      });
      fetchRequests(); // Refresh request list after updating status
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  return (
    <div>
      <button
        onClick={handleAddProduct}
        className="bg-base-200 text-white px-6 py-3 rounded hover:bg-gray-700 transition duration-300 mt-4"
      >
        Request Inventory
      </button>

      {isModalOpen && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-8 rounded-lg shadow-lg w-96 relative z-60">
            <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">
              Request Inventory
            </h2>

            {/* Input form to add a new request */}
            <form>
              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  From Branch ID
                </label>
                <input
                  type="text"
                  className="w-full p-3 border border-gray-300 rounded bg-white"
                  value={newRequest.frombranchid}
                  onChange={(e) =>
                    setNewRequest({ ...newRequest, frombranchid: e.target.value })
                  }
                />
              </div>

              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  To Branch ID
                </label>
                <input
                  type="text"
                  className="w-full p-3 border border-gray-300 rounded bg-white"
                  value={newRequest.tobranchid}
                  onChange={(e) =>
                    setNewRequest({ ...newRequest, tobranchid: e.target.value })
                  }
                />
              </div>

              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  Product ID
                </label>
                <input
                  type="text"
                  className="w-full p-3 border border-gray-300 rounded bg-white"
                  value={newRequest.productid}
                  onChange={(e) =>
                    setNewRequest({ ...newRequest, productid: e.target.value })
                  }
                />
              </div>

              <div className="mb-4">
                <label className="block text-gray-600 font-medium mb-2">
                  Quantity
                </label>
                <input
                  type="number"
                  className="w-full p-3 border border-gray-300 rounded bg-white"
                  value={newRequest.quantity}
                  onChange={(e) =>
                    setNewRequest({ ...newRequest, quantity: parseInt(e.target.value) })
                  }
                />
              </div>

              <button
                type="button"
                className="w-full bg-base-200 text-white font-medium py-3 rounded hover:bg-gray-700 transition duration-300"
                onClick={handleRequestSubmit}
              >
                Add Request
              </button>
            </form>

            {/* Display the requests in a table */}
            <div className="overflow-x-auto mt-6">
              <table className="table w-full">
                <thead>
                  <tr>
                    <th>Request ID</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {/* Ensure requests is an array before mapping */}
                  {Array.isArray(requests) &&
                    requests.map((request) => (
                      <tr key={request.requestid}>
                        <td>{request.requestid}</td>
                        <td>{request.status}</td>
                        <td>
                          {request.status === "pending" && (
                            <button
                              onClick={() => handleUpdateStatus(request.requestid)}
                              className="bg-base-200 text-white font-medium px-4 py-2 rounded hover:bg-gray-700"
                            >
                              Mark as Complete
                            </button>
                          )}
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>

            <button
              onClick={handleCloseModal}
              className="absolute top-2 right-2 text-gray-500 text-2xl"
            >
              &times;
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RequestInventory;
