import React, { useState, useEffect } from "react";
import axios from "axios";

const RequestInventory = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [requests, setRequests] = useState([]);
  const [branches, setBranches] = useState([]);
  const [products, setProducts] = useState([]);
  const [newRequest, setNewRequest] = useState({
    frombranchid: "",
    tobranchid: "",
    productid: "",
    quantity: 0,
    status: "pending",
  });

  const fetchRequests = async () => {
    try {
      const response = await axios.get("http://localhost:5050/Requests");
      setRequests(response.data.Data || []);
    } catch (err) {
      console.error("Error fetching requests:", err);
    }
  };

  const fetchBranches = async () => {
    try {
      const response = await axios.get("http://localhost:5050/branches");
      setBranches(response.data.Data || []);
    } catch (err) {
      console.error("Error fetching branches:", err);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await axios.get("http://localhost:5050/products");
      setProducts(response.data.Data || []);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  useEffect(() => {
    fetchBranches();
    fetchProducts();
    fetchRequests();
  }, []);

  const handleAddRequest = async () => {
    try {
      await axios.post("http://localhost:5050/Requests", newRequest);
      fetchRequests();
      setNewRequest({
        frombranchid: "",
        tobranchid: "",
        productid: "",
        quantity: 0,
        status: "pending",
      });
    } catch (err) {
      console.error("Error adding request:", err);
    }
  };

  const handleUpdateStatus = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5050/Requests/${requestId}`, {
        status: "complete",
      });
      fetchRequests();
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  return (
    <div>
      <button
        onClick={() => setIsModalOpen(true)}
        className="bg-blue-500 text-white px-6 py-3 rounded hover:bg-blue-600 transition duration-300 mt-4"
      >
        Request Inventory
      </button>

      {isModalOpen && (
        <div
          className="fixed inset-0 items-center bg-black bg-opacity-50 z-50 overflow-y-auto"
          onClick={(e) => e.target === e.currentTarget && setIsModalOpen(false)}
        >
          <div className="bg-white p-8 rounded-lg shadow-lg max-w-7xl mx-auto m-10 relative z-60">
            <h2 className="text-2xl font-bold text-center text-gray-700 mb-6">
              Request Inventory Management
            </h2>

            <div className="flex flex-col space-y-8">
              {/* ฟอร์มสำหรับเพิ่มคำขอสินค้า */}
              <div>
                <h3 className="text-xl font-semibold text-gray-700 mb-4">Add New Request</h3>
                <form>
                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-gray-600 font-medium mb-2">
                        From Branch
                      </label>
                      <select
                        className="w-full p-3 border border-gray-300 rounded bg-white"
                        value={newRequest.frombranchid}
                        onChange={(e) =>
                          setNewRequest({ ...newRequest, frombranchid: e.target.value })
                        }
                      >
                        <option value="">Select Branch</option>
                        {branches.map((branch) => (
                          <option key={branch.branchid} value={branch.branchid}>
                            {branch.branchname}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div>
                      <label className="block text-gray-600 font-medium mb-2">
                        To Branch
                      </label>
                      <select
                        className="w-full p-3 border border-gray-300 rounded bg-white"
                        value={newRequest.tobranchid}
                        onChange={(e) =>
                          setNewRequest({ ...newRequest, tobranchid: e.target.value })
                        }
                      >
                        <option value="">Select Branch</option>
                        {branches.map((branch) => (
                          <option key={branch.branchid} value={branch.branchid}>
                            {branch.branchname}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className="grid grid-cols-2 gap-4 mb-4">
                    <div>
                      <label className="block text-gray-600 font-medium mb-2">
                        Product
                      </label>
                      <select
                        className="w-full p-3 border border-gray-300 rounded bg-white"
                        value={newRequest.productid}
                        onChange={(e) =>
                          setNewRequest({ ...newRequest, productid: e.target.value })
                        }
                      >
                        <option value="">Select Product</option>
                        {products.map((product) => (
                          <option key={product.productid} value={product.productid}>
                            {product.productname}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div>
                      <label className="block text-gray-600 font-medium mb-2">
                        Quantity
                      </label>
                      <input
                        type="number"
                        className="w-full p-3 border border-gray-300 rounded bg-white"
                        value={newRequest.quantity}
                        onChange={(e) =>
                          setNewRequest({
                            ...newRequest,
                            quantity: parseInt(e.target.value),
                          })
                        }
                      />
                    </div>
                  </div>
                  <button
                    type="button"
                    className="bg-green-500 text-white font-medium py-3 px-6 rounded hover:bg-green-600 transition duration-300"
                    onClick={handleAddRequest}
                  >
                    Add Request
                  </button>
                </form>
              </div>

              {/* ตาราง Requests พร้อมปุ่ม Update Status */}
              <div>
                <h3 className="text-xl font-semibold text-gray-700 mb-4">Requests</h3>
                <table className="table-auto w-full border-collapse border border-gray-300">
                  <thead>
                    <tr>
                      <th className="border border-gray-300 px-4 py-2">Request ID</th>
                      <th className="border border-gray-300 px-4 py-2">From Branch</th>
                      <th className="border border-gray-300 px-4 py-2">To Branch</th>
                      <th className="border border-gray-300 px-4 py-2">Product</th>
                      <th className="border border-gray-300 px-4 py-2">Quantity</th>
                      <th className="border border-gray-300 px-4 py-2">Status</th>
                      <th className="border border-gray-300 px-4 py-2">Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {requests.map((request) => (
                      <tr key={request.requestid}>
                        <td className="border border-gray-300 px-4 py-2">{request.requestid}</td>
                        <td className="border border-gray-300 px-4 py-2">
                          {branches.find((b) => b.branchid === request.frombranchid)?.branchname ||
                            "Unknown"}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          {branches.find((b) => b.branchid === request.tobranchid)?.branchname ||
                            "Unknown"}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">
                          {products.find((p) => p.productid === request.productid)?.productname ||
                            "Unknown"}
                        </td>
                        <td className="border border-gray-300 px-4 py-2">{request.quantity}</td>
                        <td className="border border-gray-300 px-4 py-2">{request.status}</td>
                        <td className="border border-gray-300 px-4 py-2">
                          {request.status === "pending" && (
                            <button
                              onClick={() => handleUpdateStatus(request.requestid)}
                              className="bg-yellow-500 text-white font-medium px-4 py-2 rounded hover:bg-yellow-600"
                            >
                              Mark as Complete
                            </button>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            <button
              onClick={() => setIsModalOpen(false)}
              className="absolute top-2 right-2 text-gray-500 text-2xl"
            >
              &times;
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default RequestInventory;
